import React, { useState, useEffect } from 'react';
import { Outlet, Route, Link } from 'react-router-dom';

import './NavBar.css';

export default function NavBar() {
    const [page, setPage] = useState(0);

    const linkText = ["Portfolio", "About Me", "All Projects"];
    const links = ["portfolio", "aboutme", "all"];


    const handleOnClick = (newPage) => {
        setPage((prevPage) => {
            prevPage = newPage;
        });
    };

    useEffect(() => {
        document.getElementById("link_" + page).classList.add("selected");
    });
    
    console.log(page);
    return (
        <div>
            <header>
                <div className="container">
                    <Link className="logo-text" to="portfolio">Arnav Bista</Link>
                    <nav>
                        <ul>
                            {linkText.map((text, index) => {
                                return (
                                    <li key={index}>
                                        <Link
                                            href={links[index]}
                                            onClick={() => handleOnClick(index)}
                                            id={"link_" + index}
                                        >{text}</Link>
                                    </li>
                                );
                            })}
                        </ul>
                    </nav>
                </div>
            </header>
            <Outlet />
        </div>
    );
}
